mapper.select:
/*작가 작품 판매전 리스트*/ SELECT A.WORK_SQ AS workSq, /*작품번호*/ A.MBR_SQ AS mbrSq, /*멤버번호*/ A.ARTST_SQ AS artstSq, /*작가번호*/ B.AUTH_SQ AS authSq, /*작가구분*/ C.ARTST_ACTVTY_NM AS artstActvtyNm, /*작가활동명*/ C.ARTST_ENGLS_NM AS artstEnglsNm, /*작가영문명*/ /*YEAR (B.MBR_BIRTH) AS artstBirthYear, 작가생년월일*/ ( CASE WHEN C.ARTST_YOD IS NULL THEN YEAR(B.MBR_BIRTH) WHEN C.ARTST_YOD = '' THEN YEAR(B.MBR_BIRTH) ELSE CONCAT(YEAR(B.MBR_BIRTH), "~", C.ARTST_YOD) END ) AS artstBirthYear, C.ARTST_PROFILE_IMG_URL AS artstProfileImgUrl, /*작가프로필사진주소*/ A.WORK_NM AS workNm, /*작품명*/ FORMAT(A.WORK_PRC, 0) AS workPrc, /*작품가격*/ A.WORK_PRODC_YEAR AS workProdcYear, /*작품제작년도*/ A.WORK_MATRL AS workMatrl, /*작품소재*/ IF(A.WORK_MAIN_IMG_URL IS NULL, A.WORK_IMG_FRT_URL, A.WORK_MAIN_IMG_URL) AS workMainImgUrl, /*작품대표이미지주소*/ A.WORK_SIZE_WIDTH AS workSizeWidth, /*작품사이즈가로*/ A.WORK_SIZE_DEPTH AS workSizeDepth, /*작품사이즈세로*/ A.WORK_PRODC_YEAR AS workProdcYear /*작품제작년도*/ FROM TBL_DA_WORK_M AS A INNER JOIN TBL_DA_MBR_M AS B ON A.MBR_SQ = B.MBR_SQ INNER JOIN TBL_DA_ARTST_M AS C ON A.ARTST_SQ = C.ARTST_SQ WHERE A.WORK_SALE_YN = 'N' AND A.WORK_OPEN_YN = 'Y' /*작품 공개 여부*/ AND A.DEL_YN = 'N' /*작품 삭제 여부 확인 */
AND A.WORK_SQ NOT IN
#{item}
AND A.ARTST_SQ = #{artstSq}
ORDER BY A.WORK_PRODC_YEAR
ORDER BY workPrc
DESC
ASC
