mapper.select:
/*작가 작품 리스트*/ SELECT WORK_NM AS workNm, WORK_MAIN_IMG_URL AS workMainImgUrl, WORK_MATRL AS workMatrl, WORK_SIZE_WIDTH AS workSizeWidth, WORK_SIZE_DEPTH AS workSizeDepth, WORK_PRODC_YEAR AS workProdcYear, WORK_SALE_YN AS workSalen,
(CASE WHEN WORK_SALE_YN = 'Y' THEN '판매가능' WHEN WORK_SALE_YN = 'N' THEN '미판매' ELSE '' END) AS WorkSaleYn, WORK_INTRDC AS workIntrdc
FROM TBL_DA_WORK_M WHERE 1=1 AND USE_YN = 'Y' AND DEL_YN = 'N' AND ARTST_SQ = #{artstSq}
