mapper.select:
/*딜 검색*/ SELECT D.DEAL_SQ AS dealSq, /*거래번호*/ A.WORK_SQ AS workSq, /*작품번호*/ A.MBR_SQ AS mbrSq, /*멤버번호*/ A.ARTST_SQ AS artstSq, /*작가번호*/ A.ARTST_ACTVTY_NM AS artstActvtyNm, /*작가활동명*/ A.ARTST_ENGLS_NM AS artstEnglsNm, /*작가영문명*/ IF(C.ARTST_YOD IS NULL OR C.ARTST_YOD = '' ,A.ARTST_BIRTH_YEAR,CONCAT(A.ARTST_BIRTH_YEAR,'~',C.ARTST_YOD)) AS artstBirthYear, /*작가생년월일*//* 작가생년월일+작가사망연도*/ C.ARTST_PROFILE_IMG_URL AS artstProfileImgUrl, /*작가프로필사진주소*/ C.ARTST_SELF_INTRO AS artstSelfIntro, /*작가소개*/ D.DEAL_TYP_CD AS dealTypCd, /*거래유형*/ D.DEAL_STTS_CD AS dealSttsCd, /*거래상태*/ A.WORK_TYP_CD AS workTypCd, /*작품타입*/ A.WORK_NM AS workNm, /*작품명*/ A.WORK_PRODC_YEAR AS workProdcYear, /*작품제작년도*/ A.WORK_MATRL AS workMatrl, /*작품소재*/ IF(A.WORK_MAIN_IMG_URL IS NULL, A.WORK_IMG_FRT_URL, A.WORK_MAIN_IMG_URL) AS workMainImgUrl, /*작품대표이미지주소*/ A.WORK_SIZE_HEIGHT AS workSizeHeight, /*작품사이즈높이*/ A.WORK_SIZE_WIDTH AS workSizeWidth, /*작품사이즈가로*/ A.WORK_SIZE_DEPTH AS workSizeDepth, /*작품사이즈세로*/ A.WORK_PRODC_YEAR AS workProdcYear, /*작품제작년도*/ (SELECT FORMAT(MAX(BID_PRC), 0) FROM TBL_DA_AUCTN_BID_H WHERE D.DEAL_SQ = DEAL_SQ) AS maxBidPrc, /*응찰가*/ FORMAT(D.DEAL_FINAL_PRC, 0) AS dealFinalPrc, /*거래최종가격*/ FORMAT(D.DEAL_STRT_PRC, 0) AS dealStrtPrc, /*거래시작가격*/ FORMAT(D.DEAL_SBID_PRC, 0) AS dealSbidPrc, /*거래낙찰가격*/ (SELECT COUNT(DEAL_SQ) FROM TBL_DA_AUCTN_BID_H WHERE D.DEAL_SQ = DEAL_SQ) AS bidCnt, /*응찰수*/ DATE_FORMAT(D.DEAL_STRT_DT, '%Y.%m.%d %H:%i') AS dealStrtDate, /*거래시작일시*/ DATE_FORMAT(D.DEAL_ENDNG_DT, '%Y.%m.%d %H:%i') AS dealEndngDate, /*거래종료일시*/ D.DEAL_ENDNG_DT AS dealEndngDateTime /*거래종료날짜시간*/ FROM TBL_DA_WORK_M AS A LEFT JOIN TBL_DA_ARTST_M AS C ON A.ARTST_SQ = C.ARTST_SQ INNER JOIN TBL_DA_DEAL_M AS D ON A.WORK_SQ = D.WORK_SQ WHERE D.DEAL_STRT_DT <= NOW() AND A.WORK_OPEN_YN = 'Y' /*작품 공개 여부*/ AND A.DEL_YN = 'N' /*작품 삭제 여부 확인 */ AND DEAL_STTS_CD NOT IN ('ST', 'FB')
AND ( A.WORK_NM LIKE CONCAT('%',#{searchKeyword},'%') OR A.WORK_PRODC_YEAR LIKE CONCAT('%',#{searchKeyword},'%') OR A.WORK_MATRL LIKE CONCAT('%',#{searchKeyword},'%') OR A.ARTST_BIRTH_YEAR LIKE CONCAT('%',#{searchKeyword},'%'))
AND A.WORK_TYP_CD = #{workTypCd}
AND D.DEAL_TYP_CD IN
#{item}
AND D.DEAL_STTS_CD IN
#{item}
AND A.WORK_SHP_CD IN
#{item}
AND A.WORK_CLSFC_CD IN
#{item}
AND A.WORK_CLR_CD IN
#{item}
ORDER BY D.DEAL_STRT_DT
ORDER BY A.WORK_PRODC_YEAR
ORDER BY bidCnt
ORDER BY maxBidPrc, dealFinalPrc
ORDER BY D.DEAL_ENDNG_DT
DESC
ASC
ORDER BY D.DEAL_STRT_DT DESC

mapper.insert:
/*거래 등록*/ INSERT INTO TBL_DA_DEAL_M ( WORK_SQ, SELL_MBR_SQ, DEAL_TYP_CD, DEAL_STTS_CD, DEAL_STRT_PRC, DEAL_STRT_DT, DEAL_ENDNG_DT
,ARTST_SQ
,DEAL_FINAL_PRC
,DEAL_SQ
) VALUES ( #{workSq}, #{mbrSq}, #{dealTypCd}, #{dealSttsCd}, #{dealStrtPrc}, #{dealStrtDt}
,DATE_ADD(DATE_FORMAT(#{dealStrtDt},'%Y-%m-%d %H') + INTERVAL CASE WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 10 THEN 10 WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 20 THEN 20 WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 30 THEN 30 WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 40 THEN 40 WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 50 THEN 50 WHEN TIME_FORMAT(#{dealStrtDt},'%i') <= 59 THEN 00 END MINUTE, INTERVAL #{dealStrtDtAddDt} DAY)
,#{dealEndngDt}
,#{artstSq}
,#{dealFinalPrc}
,#{dealSq}
) ON DUPLICATE KEY UPDATE WORK_SQ = #{workSq}, SELL_MBR_SQ = #{mbrSq}, DEAL_TYP_CD = #{dealTypCd}, DEAL_STTS_CD = #{dealSttsCd}, DEAL_STRT_PRC = #{dealStrtPrc}, DEAL_STRT_DT = #{dealStrtDt}
,DEAL_ENDNG_DT = DATE_ADD(DATE_FORMAT(#{dealStrtDt},'%Y-%m-%d %H') + INTERVAL CASE WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 10 THEN 10 WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 20 THEN 20 WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 30 THEN 30 WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 40 THEN 40 WHEN TIME_FORMAT(#{dealStrtDt},'%i') < 50 THEN 50 WHEN TIME_FORMAT(#{dealStrtDt},'%i') <= 59 THEN 00 END MINUTE, INTERVAL #{dealStrtDtAddDt} DAY)
,DEAL_ENDNG_DT = #{dealEndngDt}
,ARTST_SQ = #{artstSq}
,DEAL_FINAL_PRC = #{dealFinalPrc}

/*응찰 테이블에 insert한다*/ INSERT INTO
TBL_DA_AUCTN_BID_H(
MBR_SQ, DEAL_SQ, BID_PRC, BID_DATE
)VALUES(
#{mbrSq}, #{dealSq}, #{bidPrc}, NOW() )

mapper.update:
/* 딜 테이블에 응찰 가격 업데이트*/
UPDATE
		TBL_DA_DEAL_M
SET
		DEAL_AUCTN_PRC = #{bidPrc}
WHERE
		DEAL_SQ = #{dealSq}

/*경매 종료된 거래 정보 업데이트*/
UPDATE 
		TBL_DA_DEAL_M
SET
		DEAL_STTS_CD = 'TC'

			,BUY_MBR_SQ = #{buyMbrSq}


			,DEAL_SBID_PRC = #{dealSbidPrc}


			,DEAL_FINAL_PRC = #{dealFinalPrc}

WHERE 
		DEAL_SQ = #{dealSq}

mapper.select:
/*작품 상세 정보 가져오기*/ SELECT A.WORK_SQ AS workSq , A.ARTST_SQ AS artstSq , A.MBR_SQ AS mbrSq , IF(A.WORK_MAIN_IMG_URL IS NULL, A.WORK_IMG_FRT_URL, A.WORK_MAIN_IMG_URL) AS workMainImgUrl /*작품대표이미지주소*/ , A.ARTST_ACTVTY_NM AS artstActvtyNm , A.WORK_NM AS workNm , A.WORK_PRODC_YEAR AS workProdcYear , A.WORK_MATRL AS workMatrl , A.WORK_FRM_YN AS workFrmYn , A.WORK_SIZE_WIDTH AS workSizeWidth , A.WORK_SIZE_DEPTH AS workSizeDepth , A.WORK_SALE_YN AS workSaleYn , A.WORK_CLR_CD AS workClrCd , A.ARTST_ENGLS_NM AS artstEnglsNm , A.WORK_IMG_FRT_URL AS workImgFrtUrl /*전면이미지주소*/ , A.WORK_IMG_RER_URL AS workImgRerUrl /*후면이미지주소*/ , A.WORK_IMG_TOP_URL AS workImgTopUrl /*상 이미지주소*/ , A.WORK_IMG_BOT_URL AS workImgBotUrl /*히 이미지주소*/ , A.WORK_IMG_LEF_URL AS workImgLefUrl /*좌 이미지주소*/ , A.WORK_IMG_RIT_URL AS workImgRitUrl /*우 이미지주소*/ , A.WORK_LAKE AS workLake , FORMAT(A.WORK_PRC, 0) AS workPrc , B.MBR_NCKNM AS mbrNcknm , A.WORK_STATE AS workState
FROM TBL_DA_WORK_M AS A INNER JOIN TBL_DA_MBR_M AS B ON A.MBR_SQ = B.MBR_SQ
WHERE 1=1 AND A.WORK_SQ = #{workSq} AND A.DEL_YN = 'N' /*작품 삭제 여부 확인 */

/*자동응찰 최고 금액 조회*/
SELECT
		AUTO_BID_PRC
FROM 
		TBL_DA_AUTO_BID_H
WHERE 
		DEAL_SQ = #{dealSq}
		AND AUTO_BID_PRC > #{bidPrc}
ORDER BY AUTO_BID_PRC DESC
LIMIT 1

mapper.update:
/*마지막 입찰자 자동응찰 빠른 순으로 변경*/
UPDATE
		TBL_DA_AUCTN_BID_H
SET
		MBR_SQ = #{mbrSq}
WHERE
		DEAL_SQ = #{dealSq}
	AND BID_PRC = #{bidPrc}

mapper.delete:
/* 거래 중단 */
DELETE 
FROM TBL_DA_DEAL_M 
WHERE DEAL_SQ = #{dealSq}

mapper.select:
/*구매확정 처리할 작품/거래 정보 전체 조회*/ SELECT A.WORK_SQ AS workSq, A.MBR_SQ AS mbrSq, A.ARTST_SQ AS artstSq, A.WORK_NM AS workNm, A.WORK_MATRL AS workMatrl, A.WORK_TYP_CD AS workTypCd, A.WORK_SHP_CD AS workShpCd, A.WORK_CLSFC_CD AS workClsfcCd, A.WORK_SIZE_WIDTH AS workSizeWidth, A.WORK_SIZE_DEPTH AS workSizeDepth, A.WORK_SIZE_HEIGHT AS workSizeHeight, A.WORK_SIZE_WEIGHT AS workSizeWeigth, A.WORK_LAKE AS workLake, A.WORK_MAIN_IMG_URL AS workMainImgUrl, A.WORK_FRM_IMG_URL AS workFrmImgUrl, A.WORK_MDIA AS workMdia, A.WORK_PRODC_YEAR AS workProdcYear, A.WORK_PRC AS workPrc, A.WORK_SALE_YN AS workSaleYn, A.WORK_INTRDC AS workIntrdc, A.WORK_CLR_CD AS workClrCd, A.WORK_THM_CD AS workThmCd, A.REG_MBR_SQ AS regMbrSq, A.REG_DT AS regDt, A.UPDT_MBR_SQ AS updtMbrSq, A.UPDT_DT AS updtDt, A.USE_YN AS UseYn, A.DEL_YN AS delYn, A.ARTST_ACTVTY_NM AS artstActvtNm, A.ARTST_ENGLS_NM AS artstEnglsNm, A.ARTST_BIRTH_YEAR AS artstBirthYear, A.WORK_IMG_FRT_URL AS workImgFrtUrl, A.WORK_IMG_RER_URL AS workImgRerUrl, A.WORK_IMG_TOP_URL AS workImgTopUrl, A.WORK_IMG_BOT_URL AS workImgBotUrl, A.WORK_IMG_LEF_URL AS workImgLefUrl, A.WORK_IMG_RIT_URL AS workImgRitUrl, A.WORK_FRM_YN AS workFrmYn, A.WORK_VIDEO_URL AS workVideoUrl, A.WORK_GRT_URL AS workGrtUrl, B.DEAL_SQ AS dealSq, B.SELL_MBR_SQ AS sellMbrSq, B.BUY_MBR_SQ AS buyMbrSq, B.DEAL_FINAL_PRC AS dealFinalPrc,
(CASE WHEN B.DEAL_ENDNG_DT >= '2022-09-01 00:00:00' AND B.DEAL_ENDNG_DT <= '2022-10-01 00:00:00' THEN 'Y' ELSE 'N' END ) AS eventYn
FROM TBL_DA_WORK_M AS A INNER JOIN TBL_DA_DEAL_M AS B ON A.WORK_SQ = B.WORK_SQ WHERE B.DEAL_SQ = #{dealSq} AND A.DEL_YN = 'N' /*작품 삭제 여부 확인 */

