mapper.select:
/*딜 검색*/ SELECT A.WORK_SQ AS workSq, /*작품번호*/ A.MBR_SQ AS mbrSq, /*멤버번호*/ A.ARTST_SQ AS artstSq, /*작가번호*/ B.AUTH_SQ AS authSq, /*작가구분*/ C.ARTST_ACTVTY_NM AS artstActvtyNm, /*작가활동명*/ C.ARTST_ENGLS_NM AS artstEnglsNm, /*작가영문명*/ YEAR (B.MBR_BIRTH) AS artstBirthYear, /*작가생년월일*/ C.ARTST_PROFILE_IMG_URL AS artstProfileImgUrl, /*작가프로필사진주소*/ D.DEAL_TYP_CD AS dealTypCd, /*거래유형*/ D.DEAL_STTS_CD AS dealSttsCd, /*거래상태*/ A.WORK_NM AS workNm, /*작품명*/ A.WORK_PRODC_YEAR AS workProdcYear, /*작품제작년도*/ A.WORK_MATRL AS workMatrl, /*작품소재*/ A.WORK_MAIN_IMG_URL AS workMainImgUrl, /*작품대표이미지주소*/ A.WORK_SIZE_HEIGHT AS workSizeHeight, /*작품사이즈높이*/ A.WORK_SIZE_WIDTH AS workSizeWidth, /*작품사이즈가로*/ A.WORK_SIZE_DEPTH AS workSizeDepth, /*작품사이즈세로*/ A.WORK_PRODC_YEAR AS workProdcYear, /*작품제작년도*/ (SELECT MAX(BID_PRC) FROM TBL_DA_AUCTN_BID_H WHERE D.DEAL_SQ = DEAL_SQ) AS maxBidPrc, /*응찰가*/ D.DEAL_STRT_PRC AS dealStrtPrc, /*거래시작가격*/ D.DEAL_SBID_PRC AS dealSbidPrc, /*거래낙찰가격*/ (SELECT COUNT(DEAL_SQ) FROM TBL_DA_AUCTN_BID_H WHERE D.DEAL_SQ = DEAL_SQ) AS bidCnt, /*응찰수*/ DATE_FORMAT(D.DEAL_STRT_DT, '%Y.%m.%d') AS dealStrtDate, /*거래시작일시*/ DATE_FORMAT(D.DEAL_ENDNG_DT, '%Y.%m.%d') AS dealEndngDate, /*거래종료일시*/ DATEDIFF(D.DEAL_ENDNG_DT, NOW()) AS dealRemainingDay, /*거래남은일자*/ DATE_FORMAT(TIMEDIFF(DATE_FORMAT(D.DEAL_ENDNG_DT, '%H:%i:%s'), DATE_FORMAT(NOW(), '%H:%i:%s')), '%H') AS dealRemainingHour, /*거래남은시*/ DATE_FORMAT(TIMEDIFF(DATE_FORMAT(D.DEAL_ENDNG_DT, '%H:%i:%s'), DATE_FORMAT(NOW(), '%H:%i:%s')), '%i') AS dealRemainingMin, /*거래남은분*/ DATE_FORMAT(TIMEDIFF(DATE_FORMAT(D.DEAL_ENDNG_DT, '%H:%i:%s'), DATE_FORMAT(NOW(), '%H:%i:%s')), '%s') AS dealRemainingSec /*거래남은초*/ FROM TBL_DA_WORK_M AS A INNER JOIN TBL_DA_MBR_M AS B ON A.MBR_SQ = B.MBR_SQ INNER JOIN TBL_DA_ARTST_M AS C ON A.ARTST_SQ = C.ARTST_SQ INNER JOIN TBL_DA_DEAL_M AS D ON A.WORK_SQ = D.WORK_SQ WHERE 1=1
AND B.AUTH_SQ = #{authSq}
AND D.DEAL_TYP_CD IN
#{item}
AND D.DEAL_STTS_CD IN
#{item}
AND A.WORK_SHP_CD IN
#{item}
AND A.WORK_CLSFC_CD IN
#{item}

mapper.insert:
/*거래 등록*/
INSERT INTO
			TBL_DA_DEAL_M (
				WORK_SQ,
				MBR_REF_NO,
				DEAL_TYP_CD,
				DEAL_STTS_CD,
				DEAL_STRT_PRC,
				DEAL_STRT_DT,
				DEAL_ENDNG_DT

					,ARTST_SQ


					,DEAL_FINAL_PRC

			) VALUES (
				#{workSq},
				CONCAT('D', LPAD(#{workSq}, 19, '0')),
				#{dealTypCd},
				#{dealSttsCd},
				#{dealStrtPrc},
				#{dealStrtDt}

					,DATE_ADD(#{dealStrtDt}, INTERVAL #{dealStrtDtAddDt} DAY)


					,#{dealEndngDt}


					,#{artstSq}


					,#{dealFinalPrc}

			) ON DUPLICATE KEY UPDATE
				DEAL_TYP_CD = #{dealTypCd},
				DEAL_STTS_CD = #{dealSttsCd},
				DEAL_STRT_PRC = #{dealStrtPrc},
				DEAL_STRT_DT = #{dealStrtDt}

					,DEAL_ENDNG_DT = DATE_ADD(#{dealStrtDt}, INTERVAL #{dealStrtDtAddDt} DAY)


					,DEAL_STRT_DT = #{dealEndngDt}


					,ARTST_SQ = #{artstSq}
