mapper.update:
UPDATE TBL_DA_MBR_M
MBR_NM =#{mbrNm}, AUTH_SQ =#{authSq}, MBR_EMAIL =#{mbrEmail}, MBR_PASSWRD =#{mbrPasswrd}, MBR_NATN_CD = #{mbrNatnCd}, MBR_BIRTH = #{mbrBirth}, MBR_SEX_CD = #{mbrSexCd}, MBR_HOME_ADDR = #{mbrHomeAddr}, MBR_DELIVRY_ADDR =#{mbrDelivryAddr}, MBR_CP_NUM = #{mbrCpNum}, UPDT_DT = NOW(), USE_YN = #{useYn}, DEL_YN = #{delYn}, MBR_EML_ALARM = #{mbrEmlAlarm}, MBR_CP_ALARM = #{mbrCpAlarm}, MBR_CP_CERT_YN = #{mbrCpCertYn}, MBR_CP_CERT_DATE = #{mbrCpCertDate},
WHERE MBR_SQ = #{mbrSq}

mapper.select:
   /* 회원 로그인 */
		SELECT 	
				MBR_SQ AS mbrSq,
				AUTH_SQ AS authSq
		FROM 	
				TBL_DA_MBR_M
		WHERE 	
				MBR_ID = #{loginId}
			AND	MBR_PASSWRD = #{loginPw}

   /* 회원 로그인 패스워드 체크 */
		SELECT 	
				COUNT(MBR_SQ)
		FROM 	
				TBL_DA_MBR_M
		WHERE 	
				MBR_ID = #{loginId}
			AND	MBR_PASSWRD = #{loginPw}

mapper.update:
/* 회원 탈퇴 */
		UPDATE 	TBL_DA_MBR_M

		SET		USE_YN = 'N',
				DEL_YN = 'Y'

		WHERE 	MBR_ID = #{email}
			AND	MBR_EMAIL = #{email}
			AND MBR_PASSWRD = #{password}

mapper.select:
/* 회원 탈퇴 여부 */
		SELECT 	
				COUNT(MBR_SQ)
		FROM 
				TBL_DA_MBR_M
		WHERE 	
				MBR_ID = #{email}
			AND	MBR_EMAIL = #{email}
			AND MBR_PASSWRD = #{password}
			AND USE_YN = 'N'
			AND DEL_YN = 'Y'

mapper.update:
UPDATE TBL_DA_ARTST_M
ARTST_ACTVTY_NM = #{artstActvtyNm}, ARTST_ENGLS_NM = #{artstEnglsNm}, ARTST_ACTVTY_PART_CD = #{artstActvtyPartCd}, ARTST_ACTVTY_NATN_CD = #{artstActvtyNatnCd}, ARTST_ACTVTY_CITY_CD = #{artstActvtyCityCd}, ARTST_SELF_INTRO = #{artstSelfIntro}, ARTST_PROFILE_IMG_URL = #{artstProfileImgUrl}, ARTST_HMPG_URL = #{artstHmpgUrl}, ARTST_PROMTN_VIDEO_URL = #{artstPromtnVideoUrl}, ARTST_FACEBOOK_URL = #{artstFacebookUrl}, ARTST_INSTAGRAM_URL = #{artstInstagramUrl}, UPDT_DT = NOW(), USE_YN = #{useYn},
WHERE MBR_SQ = #{mbrSq}

mapper.select:
SELECT
EDUCTN_SQ AS eductnSq ,ARTST_SQ AS artstSq ,MBR_SQ AS mbrSq ,EDUCTN_NM AS eductnNm ,EDUCTN_MAJOR AS eductnMajor
FROM TBL_DA_ARTST_EDUCTN_S WHERE 1=1 AND ARTST_SQ = #{artstSq} AND MBR_SQ = #{mbrSq}

mapper.update:
UPDATE 	
	TBL_DA_ARTST_EDUCTN_S
SET		
	EDUCTN_NM = #{eductnNm},
	EDUCTN_MAJOR = #{eductnMajor}
WHERE 	EDUCTN_SQ = #{eductnSq}

mapper.select:
SELECT
CAREER_SQ AS careerSq ,ARTST_SQ AS artstSq ,MBR_SQ AS mbrSq ,CAREER_NM AS careerNm ,CAREER_TYP_CD AS careerTypCd
FROM TBL_DA_ARTST_CAREER_S WHERE 1=1 AND ARTST_SQ = #{artstSq} AND MBR_SQ = #{mbrSq} AND CAREER_TYP_CD = #{careerTypCd}

mapper.update:
UPDATE 	
	TBL_DA_ARTST_CAREER_S
SET		
	CAREER_NM = #{careerNm}
WHERE 	
	CAREER_SQ = #{careerSq}

mapper.select:
SELECT
EXHBTN_SQ AS exhbtnSq ,ARTST_SQ AS artstSq ,MBR_SQ AS mbrSq ,EXHBTN_NM AS exhbtnNm ,EXHBTN_TYP_CD AS exhbtnTypCd
FROM TBL_DA_ARTST_EXHBTN_S WHERE 1=1 AND ARTST_SQ = #{artstSq} AND MBR_SQ = #{mbrSq} AND EXHBTN_TYP_CD = #{exhbtnTypCd}

