mapper.insert:
   		/*회원 스크랩 추가*/
   		INSERT INTO 
   					TBL_DA_MBR_SCRAP_R (
						MBR_SQ,
						WORK_SQ
   					) VALUES (
   						#{mbrSq},
   						#{workSq}
   					)

mapper.delete:
  		/*회원 스크랩 삭제*/
  		DELETE	FROM 
					TBL_DA_MBR_SCRAP_R
  				WHERE
  					MBR_SQ = #{mbrSq}
  				AND WORK_SQ = #{workSq}

mapper.update:
   		/*자동 로그인 정보 수정 등록*/
   		UPDATE TBL_DA_MBR_M 
   				SET
						AUTO_LOGIN_YN = #{autoLoginYn},
						AUTO_LOGIN_SSN_ID = #{autoLoginSsnId},
						AUTO_LOGIN_DATE = #{autoLoginDate}
				WHERE
						MBR_SQ = #{mbrSq}

mapper.select:
   		/*회원 변경 비밀번호 중복체크*/
   		SELECT 
				COUNT(MBR_ID)
		FROM
				TBL_DA_MBR_M
		WHERE 
				MBR_ID = #{mbrId}
		AND		MBR_PASSWRD = #{mbrPasswrd}

mapper.update:
   		/*회원 비밀번호 변경*/
   		UPDATE 
				TBL_DA_MBR_M 
		SET
				MBR_PASSWRD = #{mbrPasswrd}
		WHERE 
				MBR_ID = #{mbrId}

 		/*회원 배송 정보 수정*/
 		UPDATE
				TBL_DA_MBR_M
 		SET
 				MBR_DELIVRY_ADDR = #{mbrDelivryAddr},
 				MBR_DELIVRY_CP_NUM = #{mbrDelivryCpNum}
 		WHERE 
 				MBR_SQ = #{mbrSq}
