mapper.update:
UPDATE TBL_DA_MBR_M
MBR_NM =#{mbrNm}, AUTH_SQ =#{authSq}, MBR_EMAIL =#{mbrEmail}, MBR_PASSWRD =#{mbrPasswrd}, MBR_NATN_CD = #{mbrNatnCd}, MBR_BIRTH = #{mbrBirth}, MBR_SEX_CD = #{mbrSexCd}, MBR_HOME_ADDR = #{mbrHomeAddr}, MBR_DELIVRY_ADDR =#{mbrDelivryAddr}, MBR_CP_NUM = #{mbrCpNum}, UPDT_DT = NOW(), USE_YN = #{useYn}, DEL_YN = #{delYn},
WHERE MBR_SQ = #{mbrSq}

mapper.select:
   /* 회원 로그인 */
		SELECT 	
				MBR_SQ AS mbrSq,
				AUTH_SQ AS authSq
		FROM 	
				TBL_DA_MBR_M
		WHERE 	
				MBR_ID = #{loginId}
			AND	MBR_EMAIL = #{loginId}
			AND	MBR_PASSWRD = #{loginPw}

mapper.update:
/* 회원 탈퇴 */
		UPDATE 	TBL_DA_MBR_M

		SET		USE_YN = 'N',
				DEL_YN = 'Y'

		WHERE 	MBR_ID = #{email}
			AND	MBR_EMAIL = #{email}
			AND MBR_PASSWRD = #{password}

mapper.select:
/* 회원 탈퇴 여부 */
		SELECT 	
				COUNT(MBR_SQ)
		FROM 
				TBL_DA_MBR_M
		WHERE 	
				MBR_ID = #{email}
			AND	MBR_EMAIL = #{email}
			AND MBR_PASSWRD = #{password}
			AND USE_YN = 'N'
			AND DEL_YN = 'Y'
