mapper.select:
/*마이페이지 알람 조회*/ SELECT NOTI.NOTI_SQ AS notiSq /*커뮤니티_순번*/ , NOTI.MBR_SQ AS mbrSq /*회원_순번*/ , NOTI.WORK_SQ AS workSq /*작품_순번*/ , NOTI.ARTST_SQ AS artstSq /*작가_순번*/ , NOTI.DEAL_SQ AS dealSq /*거래_순번*/ , NOTI.NOTI_CAT_CD AS notiCatCd /*알림_유형_코드*/ , (SELECT DTL_CD_NM FROM TBL_DA_COMN_DTL_CD_S WHERE DTL_CD = NOTI.NOTI_CAT_CD) AS notiCatCdNm /*알림_유형_코드_이름*/ , NOTI.QUESTION_MBR_SQ AS questionMbrSq /*문의_회원_순번*/ , NOTI.QUESTION_CONTENT AS questionContent /*문의_내용*/ , DATE_FORMAT(NOTI.REG_DT, '%Y-%m-%d %H:%i') AS regDt /*등록_일시*/ , NOTI.REG_MBR_SQ AS regMbrSq /*등록_회원_순번*/ , DATE_FORMAT(NOTI.UPDT_DT, '%Y-%m-%d %H:%i') AS updtDt /*수정_일시*/ , CASE WHEN TIMESTAMPDIFF(HOUR, NOTI.UPDT_DT, NOW()) > 24 THEN CONCAT(CAST(TIMESTAMPDIFF(DAY, NOTI.UPDT_DT, NOW()) AS CHAR(10)), "일전") ELSE CONCAT(CAST(TIMESTAMPDIFF(HOUR, NOTI.UPDT_DT, NOW()) AS CHAR(10)), "시간전") END AS time , NOTI.UPDT_MBR_SQ AS updtMbrSq /*수정_회원_순번*/ , WORKS.WORK_NM AS workNm /*작품_명*/ , WORKS.ARTST_ACTVTY_NM AS artstActvtyNm /*작가_활동_명*/ , WORKS.ARTST_ENGLS_NM AS artstEnglsNm /*작가_영문_명*/ , WORKS.WORK_MAIN_IMG_URL AS workMainImgUrl /*작품_대표_이미지_URL*/ , IFNULL(FORMAT(DEAL.DEAL_STRT_PRC, 0), 0) AS dealStrtPrc /*거래_시작_가격*/ , MBR.MBR_NCKNM AS mbrNckNm /*회원_닉네임*/ , MBR.MBR_PROFILE_IMG_URL AS mbrProfileImgUrl /*회원_프로필_이미지_URL*/ FROM TBL_DA_NOTI_M AS NOTI LEFT JOIN TBL_DA_WORK_M AS WORKS ON NOTI.WORK_SQ = WORKS.WORK_SQ LEFT JOIN TBL_DA_DEAL_M AS DEAL ON NOTI.DEAL_SQ = DEAL.DEAL_SQ LEFT JOIN TBL_DA_MBR_M MBR ON NOTI.MBR_SQ = MBR.MBR_SQ WHERE 1=1
AND NOTI.MBR_SQ = #{mbrSq}
AND NOTI.NOTI_CAT_CD = #{notiCatCd}
AND NOTI.NOTI_TYP_CD = 'GEN' ORDER BY NOTI.REG_DT DESC

/*마이페이지 알람 개수 조회*/
SELECT COUNT(*) 
FROM TBL_DA_NOTI_M AS NOTI
WHERE 1=1

AND NOTI.MBR_SQ = #{mbrSq}


AND NOTI.NOTI_CAT_CD = #{notiCatCd}

AND NOTI.NOTI_TYP_CD = 'GEN'

/*마이페이지 팔로잉 조회*/ SELECT (CASE WHEN MBR.MBR_PROFILE_IMG_URL != NULL AND '' THEN MBR.MBR_PROFILE_IMG_URL ELSE ARTST.ARTST_PROFILE_IMG_URL END) AS mbrProfileImgUrl /*회원_프로필_이미지_URL*/ , MBR.MBR_NCKNM AS mbrNckNm /*회원_닉네임*/ , MBR.MBR_INTRO AS mbrIntro /*회원_자기소개*/ , FOLLOW.FOLLOWING_SQ AS mbrSq /*회원_순번*/ FROM TBL_DA_FOLLOWING_M AS FOLLOW LEFT JOIN TBL_DA_MBR_M AS MBR ON FOLLOW.FOLLOWING_SQ = MBR.MBR_SQ LEFT JOIN TBL_DA_ARTST_M AS ARTST ON MBR.MBR_SQ = ARTST.MBR_SQ WHERE 1=1
AND FOLLOW.MBR_SQ = #{mbrSq}
AND MBR.AUTH_SQ = #{authSq}
AND MBR.MBR_NCKNM LIKE CONCAT('%',#{searchWord},'%')
ORDER BY FOLLOW.FOLLOWING_SQ DESC LIMIT #{limit}

/*마이페이지 팔로잉 개수 조회*/
SELECT COUNT(*) 
FROM TBL_DA_FOLLOWING_M AS FOLLOW
     LEFT JOIN TBL_DA_MBR_M AS MBR
     ON FOLLOW.FOLLOWING_SQ = MBR.MBR_SQ
WHERE 1=1
  
	AND FOLLOW.MBR_SQ = #{mbrSq}
  
  
	AND MBR.AUTH_SQ = #{authSq}
  
  
  AND MBR.MBR_NCKNM LIKE CONCAT('%',#{searchWord},'%')

/* 마이페이지 스크랩 작품 */ SELECT SCRAP.MBR_SQ AS mbrSq , SCRAP.WORK_SQ AS workSq , WORKS.WORK_NM AS workNm , WORKS.WORK_PRODC_YEAR AS workProdcYear , WORKS.WORK_MATRL AS workMatrl , WORKS.WORK_SIZE_WIDTH AS workSizeWidth , WORKS.WORK_SIZE_DEPTH AS workSizeDepth , ARTST.ARTST_PROFILE_IMG_URL AS artstProfileImgUrl , IF(WORKS.ARTST_ACTVTY_NM IS NOT NULL AND WORKS.ARTST_ACTVTY_NM != '', WORKS.ARTST_ACTVTY_NM, WORKS.ARTST_ENGLS_NM) AS artstActvtyNm , IF(WORKS.ARTST_YOD IS NOT NULL AND WORKS.ARTST_YOD != '' , CONCAT(WORKS.ARTST_BIRTH_YEAR, '~', WORKS.ARTST_YOD), WORKS.ARTST_BIRTH_YEAR) AS artstBirthYear , IF(WORKS.WORK_MAIN_IMG_URL IS NOT NULL AND WORKS.WORK_MAIN_IMG_URL != '', WORKS.WORK_MAIN_IMG_URL, WORKS.WORK_IMG_FRT_URL) AS workMainImgUrl FROM TBL_DA_MBR_SCRAP_R AS SCRAP LEFT JOIN TBL_DA_WORK_M AS WORKS ON SCRAP.WORK_SQ = WORKS.WORK_SQ LEFT JOIN TBL_DA_ARTST_M AS ARTST ON WORKS.ARTST_SQ = ARTST.ARTST_SQ WHERE SCRAP.WORK_SQ IS NOT NULL AND WORKS.WORK_OPEN_YN = 'Y' AND WORKS.USE_YN = 'Y' AND WORKS.DEL_YN = 'N' AND SCRAP.MBR_SQ = #{mbrSq}
AND ( WORKS.WORK_NM LIKE CONCAT('%',#{searchWord},'%') OR WORKS.ARTST_ACTVTY_NM LIKE CONCAT('%',#{searchWord},'%') OR WORKS.ARTST_ENGLS_NM LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY SCRAP.REG_DT DESC LIMIT #{limit}

/* 마이페이지 스크랩 거래 */ SELECT SCRAP.MBR_SQ AS mbrSq , SCRAP.DEAL_SQ AS dealSq , DEAL.DEAL_TYP_CD AS dealTypCd /*거래유형*/ , DEAL.DEAL_STTS_CD AS dealSttsCd /*거래상태*/ , (SELECT FORMAT(MAX(BID_PRC), 0) FROM TBL_DA_AUCTN_BID_H WHERE DEAL.DEAL_SQ = DEAL_SQ) AS maxBidPrc /*응찰가*/ , FORMAT(DEAL.DEAL_FINAL_PRC, 0) AS dealFinalPrc /*거래최종가격*/ , FORMAT(DEAL.DEAL_STRT_PRC, 0) AS dealStrtPrc /*거래시작가격*/ , FORMAT(DEAL.DEAL_SBID_PRC, 0) AS dealSbidPrc /*거래낙찰가격*/ , (SELECT COUNT(DEAL_SQ) FROM TBL_DA_AUCTN_BID_H WHERE DEAL.DEAL_SQ = DEAL_SQ) AS bidCnt /*응찰수*/ , DATE_FORMAT(DEAL.DEAL_STRT_DT, '%Y.%m.%d %H:%i') AS dealStrtDate /*거래시작일시*/ , DATE_FORMAT(DEAL.DEAL_ENDNG_DT, '%Y.%m.%d %H:%i') AS dealEndngDate /*거래종료일시*/ , DEAL.DEAL_ENDNG_DT AS dealEndngDateTime /*거래종료날짜시간*/ , WORKS.WORK_NM AS workNm , WORKS.WORK_PRODC_YEAR AS workProdcYear , WORKS.WORK_MATRL AS workMatrl , WORKS.WORK_SIZE_WIDTH AS workSizeWidth , WORKS.WORK_SIZE_DEPTH AS workSizeDepth , ARTST.ARTST_PROFILE_IMG_URL AS artstProfileImgUrl , ARTST.ARTST_PROFILE_IMG_URL AS artstProfileImgUrl , IF(WORKS.ARTST_ACTVTY_NM IS NOT NULL AND WORKS.ARTST_ACTVTY_NM != '', WORKS.ARTST_ACTVTY_NM, WORKS.ARTST_ENGLS_NM) AS artstActvtyNm , IF(WORKS.ARTST_YOD IS NOT NULL AND WORKS.ARTST_YOD != '' , CONCAT(WORKS.ARTST_BIRTH_YEAR, '~', WORKS.ARTST_YOD), WORKS.ARTST_BIRTH_YEAR) AS artstBirthYear , IF(WORKS.WORK_MAIN_IMG_URL IS NOT NULL AND WORKS.WORK_MAIN_IMG_URL != '', WORKS.WORK_MAIN_IMG_URL, WORKS.WORK_IMG_FRT_URL) AS workMainImgUrl FROM TBL_DA_MBR_SCRAP_R AS SCRAP LEFT JOIN TBL_DA_DEAL_M AS DEAL ON SCRAP.DEAL_SQ = DEAL.DEAL_SQ LEFT JOIN TBL_DA_WORK_M AS WORKS ON DEAL.WORK_SQ = WORKS.WORK_SQ LEFT JOIN TBL_DA_ARTST_M AS ARTST ON DEAL.ARTST_SQ = ARTST.ARTST_SQ WHERE SCRAP.DEAL_SQ IS NOT NULL AND SCRAP.MBR_SQ = #{mbrSq}
AND ( WORKS.WORK_NM LIKE CONCAT('%',#{searchWord},'%') OR WORKS.ARTST_ACTVTY_NM LIKE CONCAT('%',#{searchWord},'%') OR WORKS.ARTST_ENGLS_NM LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY SCRAP.REG_DT DESC LIMIT #{limit}

/* 마이페이지 스크랩 커뮤니티 */ SELECT SCRAP.MBR_SQ AS mbrSq , COMT.COMT_SQ AS comtSq , COMT.MBR_SQ AS regMbrSq , MBR.MBR_PROFILE_IMG_URL AS mbrProfileImgUrl , MBR.MBR_NCKNM AS mbrNcknm , MBR.MBR_INTRO AS mbrIntro , COMT.COMT_TYP_CD AS comtTypCd , (SELECT DTL_CD_NM FROM TBL_DA_COMN_DTL_CD_S WHERE DTL_CD = COMT.COMT_TYP_CD) AS comtTypCdNm , COMT.COMT_IMG_URL AS comtImgUrl , COMT.COMT_TITLE AS comtTitle , COMT.COMT_CONTENT AS comtContent , COMT.COMT_LIKES AS comtLikes , COMT.COMT_VIEWS AS comtViews , COMT.COMT_CMTS AS comtCmts , COMT.COMT_SCRAPS AS comtScraps , COMT.EXHIBIT_TYP_CD AS exhibitTypCd , (SELECT DTL_CD_NM FROM TBL_DA_COMN_DTL_CD_S WHERE DTL_CD = COMT.EXHIBIT_TYP_CD ) AS exhibitTypCdNm , COMT.EXHIBIT_REGION_CD AS exhibitRegionCd , (SELECT DTL_CD_NM FROM TBL_DA_COMN_DTL_CD_S WHERE DTL_CD = COMT.EXHIBIT_REGION_CD) AS exhibitRegionCdNm FROM TBL_DA_MBR_SCRAP_R AS SCRAP LEFT JOIN TBL_DA_COMT_M AS COMT ON SCRAP.COMT_SQ = COMT.COMT_SQ LEFT JOIN TBL_DA_MBR_M AS MBR ON MBR.MBR_SQ = COMT.MBR_SQ WHERE SCRAP.COMT_SQ IS NOT NULL AND SCRAP.MBR_SQ = #{mbrSq} AND COMT.COMT_TYP_CD = #{scrapGbCd} AND COMT.DEL_YN = 'N' AND COMT.USE_YN = 'Y'
AND COMT.COMT_TITLE LIKE CONCAT('%',#{searchWord},'%')
ORDER BY SCRAP.REG_DT DESC LIMIT #{limit}

/*마이페이지 매거진 스크랩 조회*/ SELECT MGZ.MGZ_SQ as mgzSq ,MGZ.MBR_SQ as mbrSq ,MGZ.MGZ_TITLE as mgzTitle ,MGZ.MGZ_DESCRPTN as mgzDescrptn ,MGZ.MGZ_MAIN_IMG_URL as mgzMainImgUrl ,MGZ.MGZ_TYP_CD as mgzTypCd ,MGZ.REG_MBR_SQ as regMbrSq ,MGZ.REG_DT as regDt ,DATE_FORMAT(MGZ.REG_DT, '%Y.%m.%d') as regDate ,MGZ.UPDT_MBR_SQ as updtMbrSq ,MGZ.UPDT_DT as updtDt ,MGZ.DEL_YN as delYn ,MGZ.USE_YN as useYn ,MGZ.MGZ_CONTENT as mgzContent FROM TBL_DA_MBR_SCRAP_R SCRAP LEFT JOIN TBL_DA_MGZ_M AS MGZ ON SCRAP.MGZ_SQ = MGZ.MGZ_SQ LEFT JOIN TBL_DA_MBR_M MBR ON MGZ.MBR_SQ = MBR.MBR_SQ WHERE SCRAP.MBR_SQ = #{mbrSq} AND (SCRAP.MGZ_SQ IS NOT NULL AND SCRAP.MGZ_SQ != '') AND (MGZ.USE_YN = 'Y' AND MGZ.DEL_YN = 'N') AND MGZ.MGZ_TYP_CD = #{scrapGbCd}
AND ( MGZ.MGZ_TITLE LIKE CONCAT('%',#{searchWord},'%') OR MBR.MBR_NCKNM LIKE CONCAT('%',#{searchWord},'%') )
ORDER BY MGZ.MGZ_SQ DESC LIMIT #{limit}

/*마이페이지 커뮤니티 스크랩 개수 조회*/ SELECT COUNT(*) FROM TBL_DA_MBR_SCRAP_R SCRAP LEFT JOIN TBL_DA_COMT_M AS COMT ON SCRAP.COMT_SQ = COMT.COMT_SQ LEFT JOIN TBL_DA_WORK_M AS WORKS ON COMT.WORK_SQ = WORKS.WORK_SQ WHERE SCRAP.MBR_SQ = #{mbrSq} AND (SCRAP.COMT_SQ IS NOT NULL AND SCRAP.COMT_SQ != '')
AND COMT.COMT_TYP_CD = #{scrapGbCd}
AND COMT.DEL_YN = 'N' AND COMT.USE_YN = 'Y'
AND ( WORKS.WORK_NM LIKE CONCAT('%',#{searchWord},'%') OR COMT.COMT_TITLE LIKE CONCAT('%',#{searchWord},'%') OR WORKS.ARTST_ACTVTY_NM LIKE CONCAT('%',#{searchWord},'%') OR WORKS.ARTST_ENGLS_NM LIKE CONCAT('%',#{searchWord},'%') )

/*마이페이지 매거진 스크랩 개수 조회*/ SELECT COUNT(*) FROM TBL_DA_MBR_SCRAP_R SCRAP LEFT JOIN TBL_DA_MGZ_M AS MGZ ON SCRAP.MGZ_SQ = MGZ.MGZ_SQ LEFT JOIN TBL_DA_MBR_M MBR ON MGZ.MBR_SQ = MBR.MBR_SQ WHERE SCRAP.MBR_SQ = #{mbrSq} AND (SCRAP.MGZ_SQ IS NOT NULL AND SCRAP.MGZ_SQ != '') AND (MGZ.USE_YN = 'Y' AND MGZ.DEL_YN = 'N')
AND MGZ.MGZ_TYP_CD = #{scrapGbCd}
AND ( MGZ.MGZ_TITLE LIKE CONCAT('%',#{searchWord},'%') OR MBR.MBR_NCKNM LIKE CONCAT('%',#{searchWord},'%') )

/*마이페이지 메인 나의 커뮤니티 총 갯수 조회*/ SELECT COUNT(COMT.COMT_SQ) AS total FROM TBL_DA_COMT_M AS COMT LEFT JOIN TBL_DA_MBR_M AS MBR ON COMT.MBR_SQ = MBR.MBR_SQ LEFT JOIN TBL_DA_WORK_M AS WORKS ON COMT.WORK_SQ = WORKS.WORK_SQ LEFT JOIN TBL_DA_ARTST_M AS ARTST ON COMT.ARTST_SQ = ARTST.ARTST_SQ LEFT JOIN TBL_DA_DEAL_M AS DEAL ON COMT.DEAL_SQ = DEAL.DEAL_SQ WHERE COMT.MBR_SQ = #{mbrSq} AND COMT.COMT_TYP_CD = #{comtTypCd} AND COMT.DEL_YN = 'N' AND COMT.USE_YN = 'Y'
AND WORKS.WORK_TYP_CD = #{workTypCd}
AND COMT.EXHIBIT_TYP_CD = #{selGubun}
AND COMT.EXHIBIT_REGION_CD = #{selRegion}

