mapper.select:
/* 쿠폰 리스트 - 결제 */ SELECT CM.CUPON_SQ as cuponSq ,CM.CUPON_DIV_CD as cuponDivCd ,( select CDCS.DTL_CD_NM from TBL_DA_COMN_DTL_CD_S CDCS where (CDCS.USE_YN='Y' and CDCS.DEL_YN='N') and CDCS.DTL_CD=CM.CUPON_DIV_CD ) as cuponDivNm ,CM.CUPON_TYP_CD as cuponTypCd ,( select CDCS.DTL_CD_NM from TBL_DA_COMN_DTL_CD_S CDCS where (CDCS.USE_YN='Y' and CDCS.DEL_YN='N') and CDCS.DTL_CD=CM.CUPON_TYP_CD ) as cuponTypNm ,CM.CUPON_DETAIL_CD as cuponDetailCd ,( select CDCS.DTL_CD_NM from TBL_DA_COMN_DTL_CD_S CDCS where (CDCS.USE_YN='Y' and CDCS.DEL_YN='N') and CDCS.DTL_CD=CM.CUPON_DETAIL_CD ) as cuponDetailNm ,CM.DISC_TYP_CD as discTypCd ,( select CDCS.DTL_CD_NM from TBL_DA_COMN_DTL_CD_S CDCS where (CDCS.USE_YN='Y' and CDCS.DEL_YN='N') and CDCS.DTL_CD=CM.DISC_TYP_CD ) as discTypNm ,CM.CUPON_DR as cuponDr ,CM.CUPON_DISC_AMT as cuponDiscAmt ,CM.CUPON_IDNTFCTN_NUM as cuponIdntfctnNum ,CM.CUPON_NM as cuponNm ,CM.CUPON_CNTNT as cuponCntnt ,DATE_FORMAT(MCR.USE_STRT_DT, '%Y.%m.%d %H:%i') as useStrtDt ,DATE_FORMAT(MCR.USE_END_DT, '%Y.%m.%d %H:%i') as useEndDt ,CM.REG_MBR_SQ as regMbrSq ,CM.REG_DT as regDt ,CM.UPDT_MBR_SQ as updtMbrSq ,CM.UPDT_DT as updtDt ,CM.USE_YN as useYn ,CM.DEL_YN as delYn ,TIMESTAMPDIFF(DAY, MCR.USE_END_DT, CURDATE()) as diffDay ,MCR.CUPON_USE_YN as cuponUseYn ,MCR.CUPON_USE_DT as cuponUseDt FROM TBL_DA_CUPON_M CM INNER JOIN TBL_DA_MBR_CUPON_R MCR ON CM.CUPON_SQ = MCR.CUPON_SQ WHERE (CM.USE_YN = 'Y' AND CM.DEL_YN = 'N') AND ( ( CM.CUPON_DIV_CD = "IU" AND NOW() <= MCR.USE_END_DT) OR ( CM.CUPON_DIV_CD = "TU" AND (NOW() >= MCR.USE_STRT_DT AND NOW() <= MCR.USE_END_DT) ) ) AND MCR.MBR_SQ = #{mbrSq} AND CM.CUPON_TYP_CD = #{cuponTypCd}
AND CM.CUPON_DETAIL_CD = #{cuponDetailCd}
AND MCR.CUPON_USE_YN = 'N'

/* 쿠폰중복등록 카운트 */
SELECT
	COUNT(*)
FROM TBL_DA_MBR_CUPON_R
WHERE CUPON_SQ = #{cuponSq} AND MBR_SQ = #{mbrSq}

/*거래내역 정찰가 리스트*/ SELECT B.DEAL_SQ AS dealSq, B.SELL_MBR_SQ AS sellMbrSq, B.BUY_MBR_SQ AS buyMbrSq, B.WORK_SQ AS workSq, B.ARTST_SQ AS artstSq, IF(A.WORK_MAIN_IMG_URL IS NULL, A.WORK_IMG_FRT_URL, A.WORK_MAIN_IMG_URL) AS workMainImgUrl, /*작품대표이미지주소*/ A.ARTST_ACTVTY_NM AS artstActvtyNm, A.WORK_NM AS workNm, A.WORK_PRODC_YEAR AS workProdcYear, B.DEAL_TYP_CD AS dealTypCd, B.DEAL_STTS_CD AS dealSttsCd, B.BUY_PAYMNT_STTS_CD AS buyPaymntSttsCd, B.SELL_PAYMNT_STTS_CD AS sellPaymntSttsCd, DATE_FORMAT(B.DEAL_STRT_DT, '%Y.%m.%d %H:%i') AS dealStrtDate, /*거래시작일시*/ DATE_FORMAT(B.DEAL_ENDNG_DT, '%Y.%m.%d %H:%i') AS dealEndngDate, /*거래종료일시*/ FORMAT(B.DEAL_STRT_PRC, 0) AS dealStrtPrc, FORMAT(B.DEAL_FINAL_PRC, 0) AS dealFinalPrc, B.DEAL_ENDNG_DT AS dealEndngDateTime /*거래종료날짜시간*/ FROM TBL_DA_WORK_M AS A INNER JOIN TBL_DA_DEAL_M AS B ON A.WORK_SQ = B.WORK_SQ WHERE B.DEAL_STRT_DT <= NOW() AND A.DEL_YN = 'N' /*작품 삭제 여부 확인 */ AND B.DEAL_TYP_CD = 'S'
AND B.BUY_MBR_SQ = #{mbrSq}
AND B.SELL_MBR_SQ = #{mbrSq}
AND (B.BUY_MBR_SQ = #{mbrSq} OR B.SELL_MBR_SQ = #{mbrSq})
AND B.DEAL_STTS_CD = 'TP'
AND B.DEAL_STTS_CD = 'TC'
AND B.DEAL_STTS_CD = 'PD'
AND B.DEAL_STTS_CD = 'DS'
AND B.DEAL_STTS_CD = 'DC'
AND B.DEAL_STTS_CD = 'PC'
AND B.DEAL_STTS_CD = 'FB'
AND ( A.ARTST_ACTVTY_NM LIKE CONCAT('%',#{searchKeyword},'%') OR A.ARTST_ENGLS_NM LIKE CONCAT('%',#{searchKeyword},'%') OR A.WORK_NM LIKE CONCAT('%',#{searchKeyword},'%') )
AND DATE_FORMAT(B.DEAL_ENDNG_DT, '%Y.%m.%d') BETWEEN #{searchStrtDate} AND #{searchEndngDate}
GROUP BY B.DEAL_SQ;

