INSERT INTO
			TBL_DA_KEYWRD_S (
				KEYWRD,
				WORK_SQ
			)
			VALUE(
				#{keywrd},
				(
					SELECT
							WORK_SQ
					FROM
							TBL_DA_WORK_M
					WHERE
							ARTST_ACTVTY_NM = #{artstActvtyNm}
