mapper.select:
/*통합 나의 작품 리스트*/ SELECT D.DEAL_SQ AS dealSq, /*거래 번호*/ A.WORK_SQ AS workSq, /*작품번호*/ A.MBR_SQ AS mbrSq, /*멤버번호*/ A.ARTST_SQ AS artstSq, /*작가번호*/ B.AUTH_SQ AS authSq, /*작가구분*/ A.WORK_TYP_CD AS workTypCd, /*작품구분*/ A.WORK_OPEN_YN AS workOpenYn, /*작품공개여부*/ A.ARTST_ACTVTY_NM AS artstActvtyNm, /*작가활동명*/ A.ARTST_ENGLS_NM AS artstEnglsNm, /*작가영문명*/ IF(A.ARTST_YOD IS NULL OR A.ARTST_YOD = '' ,A.ARTST_BIRTH_YEAR,CONCAT(A.ARTST_BIRTH_YEAR, '~' ,A.ARTST_YOD)) AS artstBirthYear, /*작가생년월일+작가사망연도*/ C.ARTST_PROFILE_IMG_URL AS artstProfileImgUrl, /*작가프로필사진주소*/ D.DEAL_TYP_CD AS dealTypCd, /*거래유형*/ D.DEAL_STTS_CD AS dealSttsCd, /*거래상태*/ A.WORK_NM AS workNm, /*작품명*/ A.WORK_PRODC_YEAR AS workProdcYear, /*작품제작년도*/ A.WORK_MATRL AS workMatrl, /*작품소재*/ IF(A.WORK_MAIN_IMG_URL IS NULL, A.WORK_IMG_FRT_URL, A.WORK_MAIN_IMG_URL) AS workMainImgUrl, /*작품대표이미지주소*/ A.WORK_SIZE_HEIGHT AS workSizeHeight, /*작품사이즈높이*/ A.WORK_SIZE_WIDTH AS workSizeWidth, /*작품사이즈가로*/ A.WORK_SIZE_DEPTH AS workSizeDepth, /*작품사이즈세로*/ A.WORK_PRODC_YEAR AS workProdcYear, /*작품제작년도*/ B.MBR_PROFILE_IMG_URL AS mbrProfileImgUrl, /*프로필 이미지*/ (SELECT FORMAT(MAX(BID_PRC), 0) FROM TBL_DA_AUCTN_BID_H WHERE D.DEAL_SQ = DEAL_SQ) AS maxBidPrc, /*응찰가*/ FORMAT(D.DEAL_STRT_PRC, 0) AS dealStrtPrc, /*거래시작가격*/ FORMAT(D.DEAL_SBID_PRC, 0) AS dealSbidPrc, /*거래낙찰가격*/ FORMAT(D.DEAL_FINAL_PRC, 0) AS dealFinalPrc, /*거래최종가격*/ (SELECT COUNT(DEAL_SQ) FROM TBL_DA_AUCTN_BID_H WHERE D.DEAL_SQ = DEAL_SQ) AS bidCnt, /*응찰수*/ DATE_FORMAT(D.DEAL_STRT_DT, '%Y.%m.%d %H:%i') AS dealStrtDate, /*거래시작일시*/ DATE_FORMAT(D.DEAL_ENDNG_DT, '%Y.%m.%d %H:%i') AS dealEndngDate, /*거래종료일시*/ D.DEAL_STRT_DT AS dealStrtDateTime, /*거래시작날짜시간*/ D.DEAL_ENDNG_DT AS dealEndngDateTime /*거래종료날짜시간*/ FROM TBL_DA_WORK_M AS A INNER JOIN TBL_DA_MBR_M AS B ON A.MBR_SQ = B.MBR_SQ LEFT JOIN TBL_DA_ARTST_M AS C ON A.ARTST_SQ = C.ARTST_SQ LEFT JOIN TBL_DA_DEAL_M AS D ON A.WORK_SQ = D.WORK_SQ AND D.DEAL_STTS_CD != 'FB' WHERE A.DEL_YN = 'N' /*작품 삭제 여부 확인 */ AND A.MBR_SQ = #{mbrSq}
AND A.WORK_TYP_CD = #{workTypCd}
AND ( A.WORK_NM LIKE CONCAT('%',#{keyword},'%') OR A.ARTST_ACTVTY_NM LIKE CONCAT('%',#{keyword},'%') OR A.ARTST_ENGLS_NM LIKE CONCAT('%',#{keyword},'%') )
AND DEAL_SQ IS NOT NULL AND DEAL_STTS_CD = 'TP'
AND DEAL_SQ IS NULL
AND A.WORK_OPEN_YN = 'Y'
AND A.WORK_OPEN_YN = 'N'
HAVING bidCnt = 0;
ORDER BY A.REG_DT ASC
ORDER BY A.REG_DT DESC

