UPDATE 
		TBL_DA_WORK_M 
SET		
		WORK_OPEN_YN = #{item.workOpenYn}
WHERE  
		WORK_SQ = #{item.workSq}

/*작품 삭제 설정*/
UPDATE 
		TBL_DA_WORK_M 
SET		
		DEL_YN = 'Y'
		, UPDT_DT = NOW()
		, UPDT_MBR_SQ = #{mbrSq}
WHERE  
		WORK_SQ = #{workSq};

