								,ARTST_YOD = #{artstYod}
							
							,WORK_OPEN_YN = #{workOpenYn}

mapper.select:
/*판매중이 아닌 스크랩 목록 조회*/ SELECT A.WORK_SQ AS workSq, /*작품번호*/ A.MBR_SQ AS mbrSq, /*멤버번호*/ A.ARTST_SQ AS artstSq, /*작가번호*/ D.AUTH_SQ AS authSq, /*작가구분*/ C.ARTST_PROFILE_IMG_URL AS artstProfileImgUrl, /*작가프로필사진주소*/ A.ARTST_ACTVTY_NM AS artstActvtyNm, /*작가활동명*/ A.ARTST_BIRTH_YEAR AS artstBirthYear, /*작가생년월일*/ A.WORK_NM AS workNm, /*작품명*/ FORMAT(A.WORK_PRC, 0) AS workPrc, /*작품가격*/ A.WORK_PRODC_YEAR AS workProdcYear, /*작품제작년도*/ A.WORK_MATRL AS workMatrl, /*작품소재*/ IF(A.WORK_MAIN_IMG_URL IS NULL, A.WORK_IMG_FRT_URL, A.WORK_MAIN_IMG_URL) AS workMainImgUrl, /*작품대표이미지주소*/ A.WORK_SIZE_WIDTH AS workSizeWidth, /*작품사이즈가로*/ A.WORK_SIZE_DEPTH AS workSizeDepth, /*작품사이즈세로*/ A.WORK_PRODC_YEAR AS workProdcYear /*작품제작년도*/ FROM TBL_DA_WORK_M AS A, TBL_DA_MBR_M AS D, TBL_DA_ARTST_M AS C WHERE A.MBR_SQ = D.MBR_SQ AND A.ARTST_SQ = C.ARTST_SQ
AND A.DEL_YN = 'N' /* 작품 삭제 여부 확인 */
AND A.WORK_SQ NOT IN
#{item}
AND A.WORK_SQ IN
#{item.workSq}

mapper.update:
/*구매 결제상태 변경*/
UPDATE 
		TBL_DA_DEAL_M 
SET		
		BUY_PAYMNT_STTS_CD = #{dealSttsCd}
WHERE  
		DEAL_SQ = #{dealSq}

/*판매 결제상태 변경*/
UPDATE 
		TBL_DA_DEAL_M 
SET		
		SELL_PAYMNT_STTS_CD = #{dealSttsCd}
WHERE  
		DEAL_SQ = #{dealSq}

mapper.select:
/*운송 가격 가져오기*/
SELECT
		FORMAT(TRNSPRT_PRC , 0)
FROM
		TBL_DA_TRNSPRT_PRC_MTRX
WHERE 
		TRNSPRT_DIV_CD = #{trnsprtDivCd}
	
	AND TRNSPRT_TYP_CD = #{trnsprtTypCd}
	
	
	AND TRNSPRT_AREA_CD = #{trnsprtAreaCd}
	
	
	AND TRNSPRT_SERVICE_CD = #{trnsprtServiceCd}

/* 운송 매트리스 코드 텍스트, 가격 조회 */
SELECT 
		TRNSPRT_SERVICE_CD_NM AS text,
		FORMAT(TRNSPRT_PRC, 0) AS value
FROM
		TBL_DA_TRNSPRT_PRC_MTRX 
WHERE 
		TRNSPRT_DIV_CD = #{trnsprtDivCd}
	
	AND TRNSPRT_TYP_CD = #{trnsprtTypCd}
	
	
	AND TRNSPRT_AREA_CD = #{trnsprtAreaCd}
	
	
	AND TRNSPRT_SERVICE_NM = #{trnsprtServiceNm}
	
	
	AND TRNSPRT_REQ_YN = #{trnsprtReqYn}

/*운송 테이블에 저장하기 위해 해당 운송 정보 가져오기*/
SELECT 
		TRNSPRT_DIV_CD AS trnsprtDivCd,
		TRNSPRT_DIV_CD_NM AS trnsprtDivCdNm,
		TRNSPRT_TYP_CD AS trnsprtTypCd,
		TRNSPRT_TYP_CD_NM AS trnsprtTypCdNm,
		TRNSPRT_AREA_CD AS trnsprtAreaCd,
		TRNSPRT_AREA_CD_NM AS trnsprtAreaCdNm,
		TRNSPRT_SERVICE_CD AS trnsprtServiceCd,
		TRNSPRT_SERVICE_CD_NM AS trnsprtServiceCdNm,
		TRNSPRT_PRC AS trnsprtPrc
FROM 
		TBL_DA_TRNSPRT_PRC_MTRX
WHERE 
		TRNSPRT_DIV_CD = #{trnsprtDivCd}
	AND	TRNSPRT_SERVICE_CD_NM = #{trnsprtServiceCdNm}
	AND TRNSPRT_PRC = #{trnsprtPrc}
	AND TRNSPRT_TYP_CD = #{trnsprtTypCd}
	
	AND TRNSPRT_AREA_CD = #{trnsprtAreaCd}

mapper.insert:
/*운송 테이블에 운송 정보 입력*/
REPLACE INTO
			TBL_DA_DEAL_TRNSPRT_S(
				DEAL_SQ
				,BUY_MBR_SQ
				,SELL_MBR_SQ
				,ARTST_SQ
				,TRNSPRT_DIV_CD
				,TRNSPRT_DIV_CD_NM
				,TRNSPRT_TYP_CD
				,TRNSPRT_TYP_CD_NM
				,TRNSPRT_REQ_YN
				,TRNSPRT_AREA_CD
				,TRNSPRT_AREA_CD_NM
				,TRNSPRT_SERVICE_CD
				,TRNSPRT_SERVICE_CD_NM
				,TRNSPRT_PRC 
			)VALUES
			
			(
				#{item.dealSq}
				,#{item.buyMbrSq}
				,#{item.sellMbrSq}
				
					
				,#{item.artstSq}	
					
					
				,NULL
					
				
				,#{item.trnsprtDivCd}
				,#{item.trnsprtDivCdNm}
				,#{item.trnsprtTypCd}
				,#{item.trnsprtTypCdNm}
				,#{item.trnsprtReqYn}
				
					
				,#{item.trnsprtAreaCd}
					
					
				,NULL
					
				
				
					
				,#{item.trnsprtAreaCdNm}
					
					
				,NULL
					
				
				,#{item.trnsprtServiceCd}
				,#{item.trnsprtServiceCdNm}
				
					
				,${item.trnsprtPrc}	
					
					
				,NULL
					
				
			)

mapper.select:
   		/* 쿠폰 할인 적용 전의 총 값 가져오기 */
   		SELECT
   				SUM(A.VALUE) AS totalAmt
   		FROM	(
   				SELECT
						TRNSPRT_SERVICE_CD_NM AS text,
						TRNSPRT_PRC AS value,
						TRNSPRT_REQ_YN AS regYn
				FROM 
						TBL_DA_TRNSPRT_PRC_MTRX
				WHERE 
						TRNSPRT_DIV_CD = #{trnsprtDivCd}
					
					AND TRNSPRT_TYP_CD = #{trnsprtTypCd}
					
					
					AND TRNSPRT_AREA_CD = #{trnsprtAreaCd}
					
					AND TRNSPRT_REQ_YN = 'Y'
				UNION ALL
					SELECT
						TRNSPRT_SERVICE_CD_NM AS text,
						TRNSPRT_PRC AS value,
						TRNSPRT_REQ_YN AS regYn
				FROM 
						TBL_DA_TRNSPRT_PRC_MTRX
				WHERE 
						TRNSPRT_DIV_CD = #{trnsprtDivCd}
					
					AND TRNSPRT_TYP_CD = #{trnsprtTypCd}
					
					
					AND TRNSPRT_AREA_CD = #{trnsprtAreaCd}
					
					
					AND TRNSPRT_SERVICE_CD_NM = #{trnsprtServiceCdNm}
					
   		) A

/* 쿠폰 할인 적용 값 가져오기 */ SELECT TRNSPRT_SERVICE_CD_NM AS text,
( CASE
WHEN TRNSPRT_SERVICE_CD = 'SF' THEN FORMAT(TRNSPRT_PRC - ${cuponDiscAmt}, 0)
WHEN TRNSPRT_SERVICE_CD = 'SF' THEN FORMAT(TRNSPRT_PRC - (TRNSPRT_PRC * (${cuponDiscAmt} * 0.01)), 0)
ELSE FORMAT(TRNSPRT_PRC, 0) END )AS value,
FORMAT(TRNSPRT_PRC, 0) AS value,
TRNSPRT_REQ_YN AS regYn FROM TBL_DA_TRNSPRT_PRC_MTRX WHERE TRNSPRT_DIV_CD = #{trnsprtDivCd}
AND TRNSPRT_TYP_CD = #{trnsprtTypCd}
AND TRNSPRT_AREA_CD = #{trnsprtAreaCd}
AND TRNSPRT_REQ_YN = 'Y' UNION ALL SELECT TRNSPRT_SERVICE_CD_NM AS text,
( CASE
WHEN TRNSPRT_SERVICE_CD = 'SF' THEN FORMAT(TRNSPRT_PRC - ${cuponDiscAmt}, 0)
WHEN TRNSPRT_SERVICE_CD = 'SF' THEN FORMAT(TRNSPRT_PRC - (TRNSPRT_PRC * (${cuponDiscAmt} * 0.01)), 0)
ELSE FORMAT(TRNSPRT_PRC, 0) END )AS value,
FORMAT(TRNSPRT_PRC, 0) AS value,
TRNSPRT_REQ_YN AS regYn FROM TBL_DA_TRNSPRT_PRC_MTRX WHERE TRNSPRT_DIV_CD = #{trnsprtDivCd}
AND TRNSPRT_TYP_CD = #{trnsprtTypCd}
AND TRNSPRT_AREA_CD = #{trnsprtAreaCd}
AND TRNSPRT_SERVICE_CD_NM = #{trnsprtServiceCdNm}

/* 쿠폰 할인 적용 총 값 가져오기 */ SELECT
SUM(A.VALUE) - ${cuponDiscAmt} AS totalAmt
SUM(A.VALUE) - (SUM(A.VALUE) * (${cuponDiscAmt} * 0.01)) AS totalAmt
SUM(A.VALUE) AS totalAmt
SUM(A.VALUE) AS totalAmt
FROM ( SELECT TRNSPRT_SERVICE_CD_NM AS text,
( CASE
WHEN TRNSPRT_SERVICE_CD = 'SF' THEN TRNSPRT_PRC - ${cuponDiscAmt}
WHEN TRNSPRT_SERVICE_CD = 'SF' THEN TRNSPRT_PRC - (TRNSPRT_PRC * (${cuponDiscAmt} * 0.01))
ELSE TRNSPRT_PRC END )AS value,
TRNSPRT_PRC AS value,
TRNSPRT_REQ_YN AS regYn FROM TBL_DA_TRNSPRT_PRC_MTRX WHERE TRNSPRT_DIV_CD = #{trnsprtDivCd}
AND TRNSPRT_TYP_CD = #{trnsprtTypCd}
AND TRNSPRT_AREA_CD = #{trnsprtAreaCd}
AND TRNSPRT_REQ_YN = 'Y' UNION ALL SELECT TRNSPRT_SERVICE_CD_NM AS text,
( CASE
WHEN TRNSPRT_SERVICE_CD = 'SF' THEN TRNSPRT_PRC - ${cuponDiscAmt}
WHEN TRNSPRT_SERVICE_CD = 'SF' THEN TRNSPRT_PRC - (TRNSPRT_PRC * (${cuponDiscAmt} * 0.01))
ELSE TRNSPRT_PRC END )AS value,
TRNSPRT_PRC AS value,
TRNSPRT_REQ_YN AS regYn FROM TBL_DA_TRNSPRT_PRC_MTRX WHERE TRNSPRT_DIV_CD = #{trnsprtDivCd}
AND TRNSPRT_TYP_CD = #{trnsprtTypCd}
AND TRNSPRT_AREA_CD = #{trnsprtAreaCd}
AND TRNSPRT_SERVICE_CD_NM = #{trnsprtServiceCdNm}
) A

/*소장품 삭제 선택시 거래 등록 여부 확인*/ SELECT COUNT(*) FROM TBL_DA_WORK_M AS A
INNER JOIN TBL_DA_DEAL_M AS B ON A.WORK_SQ = B.WORK_SQ
WHERE 1=1 AND A.WORK_SQ = #{workSq}

mapper.update:
/* 소장품 삭제 */
UPDATE 
		TBL_DA_WORK_M 
SET		
		DEL_YN = 'Y'
		, UPDT_DT = NOW()
		, UPDT_MBR_SQ = #{mbrSq}
WHERE  
		WORK_SQ = #{workSq}

mapper.insert:
INSERT INTO TBL_DA_COMT_KEYWRDS_R(
	COMT_SQ,
	COMT_KEYWRD
) 
