mapper.update:
   		/*커뮤니티 키워드 수정*/
		UPDATE 
				TBL_DA_COMT_KEYWRDS_R 
		SET		
				COMT_KEYwRD = #{comtKeywrd}
		WHERE  
				COMT_SQ = #{comtSq}

mapper.select:
/*마이페이지 커뮤니티 관련 리스트 조회*/ SELECT COMT.COMT_SQ AS comtSq /*커뮤니티_순번*/ , COMT.MBR_SQ AS mbrSq /*회원_순번*/ , COMT.WORK_SQ AS workSq /*작품_순번*/ , COMT.ARTST_SQ AS artstSq /*작가_순번*/ , COMT.DEAL_SQ AS dealSq /*거래_순번*/ , COMT.COMT_TYP_CD AS comtTypCd /*커뮤니티_타입_코드*/ , CDDTL.DTL_CD_NM AS comtTypCdNm /*커뮤니티_타입_코드_이름*/ , COMT.COMT_IMG_URL AS comtImgUrl /*커뮤니티_이미지_URL*/ , COMT.COMT_TITLE AS comtTitle /*커뮤니티_제목*/ , IFNULL((SELECT COUNT(CMT_SQ) FROM TBL_DA_COMT_CMTS_R WHERE COMT.COMT_SQ = COMT_SQ), 0) + IFNULL((SELECT COUNT(REPLY_SQ) FROM TBL_DA_COMT_REPLYS_R WHERE COMT.COMT_SQ = COMT_SQ), 0) AS cmtTotalCount /*커뮤니티_댓글_총갯수*/ , COMT.REG_DT AS regDt /*등록_일시*/ , COMT.REG_MBR_SQ AS regMbrSq /*등록_회원_순번*/ , COMT.UPDT_DT AS updtDt /*수정_일시*/ , COMT.UPDT_MBR_SQ AS updtMbrSq /*수정_회원_순번*/ , COMT.COMT_LIKES AS cmtLikes /*커뮤니티글_좋아요*/ , COMT.COMT_SCRAPS AS cmtScraps /*커뮤니티글_스크랩*/ , COMT.COMT_VIEWS AS cmtViews /*커뮤니티글_스크랩*/ , COMT.OPEN_YN AS cmtOpenYn /*커뮤니티글_공개 유무*/ , COMT.COMT_CONTENT AS cmtContent /*커뮤니티글_내용*/ , WORKS.ARTST_ACTVTY_NM AS artstActvtyNm /*작가활동명*/ , WORKS.WORK_NM AS workNm /*작품_이름*/ , ARTST.ARTST_PROFILE_IMG_URL AS artstProfileImgUrl /*작가프로필사진주소*/ , MBR.MBR_NCKNM AS mbrNcknm /*회원_닉네임*/ , MBR.MBR_INTRO AS mbrIntro /*회원_자기소개*/ , MBR.MBR_PROFILE_IMG_URL AS mbrProfileImgUrl /*회원_자기소개*/
FROM TBL_DA_COMT_M AS COMT LEFT JOIN TBL_DA_MBR_M AS MBR ON COMT.MBR_SQ = MBR.MBR_SQ LEFT JOIN TBL_DA_WORK_M AS WORKS ON COMT.WORK_SQ = WORKS.WORK_SQ LEFT JOIN TBL_DA_ARTST_M AS ARTST ON COMT.ARTST_SQ = ARTST.ARTST_SQ LEFT JOIN TBL_DA_DEAL_M AS DEAL ON COMT.DEAL_SQ = DEAL.DEAL_SQ LEFT JOIN TBL_DA_COMN_CD_M AS CD ON CD.CD = 'COMT_TYP_CD' LEFT JOIN TBL_DA_COMN_DTL_CD_S AS CDDTL ON CD.CD_SQ = CDDTL.CD_SQ AND CDDTL.DTL_CD = COMT.COMT_TYP_CD WHERE COMT.MBR_SQ = #{mbrSq} AND COMT.COMT_TYP_CD = #{comtTypCd} AND COMT.DEL_YN = 'N' AND COMT.USE_YN = 'Y'
AND WORKS.WORK_TYP_CD = #{workTypCd}
AND ( WORKS.WORK_NM LIKE CONCAT('%',#{keyword},'%') OR COMT.COMT_TITLE LIKE CONCAT('%',#{keyword},'%') OR WORKS.ARTST_ACTVTY_NM LIKE CONCAT('%',#{keyword},'%') OR WORKS.ARTST_ENGLS_NM LIKE CONCAT('%',#{keyword},'%') )
AND COMT.EXHIBIT_TYP_CD = #{selGubun}
AND COMT.EXHIBIT_REGION_CD = #{selRegion}
AND COMT.OPEN_YN = 'Y'
AND COMT.OPEN_YN = 'N'
ORDER BY COMT.REG_DT ASC
ORDER BY COMT.REG_DT DESC
AND COMT.REG_DT >= DATE_ADD(NOW(), INTERVAL -1 YEAR) ORDER BY COMT.COMT_LIKES DESC
ORDER BY COMT.COMT_LIKES ASC
ORDER BY COMT.REG_DT DESC
LIMIT ${startIndex}, ${searchStep}
LIMIT ${limit} OFFSET ${offset}

mapper.update:
/*작품 공개/비공개 설정*/
UPDATE 
		TBL_DA_COMT_M 
SET		
		OPEN_YN = #{comtOpenYn}
WHERE  
		COMT_SQ = #{comtSq}

/*커뮤니티글 삭제 설정*/
UPDATE 
		TBL_DA_COMT_M 
SET		
		DEL_YN = 'Y'
		, UPDT_DT = NOW()
		, UPDT_MBR_SQ = #{mbrSq}
WHERE  
		COMT_SQ = #{comtSq};
