mapper.select:
SELECT MBR_SQ AS mbrSq ,ARTST_SQ AS artstSq ,ARTST_ACTVTY_NM AS artstActvtyNm ,ARTST_ENGLS_NM AS artstEnglsNm ,ARTST_ACTVTY_PART_CD AS artstActvtyPartCd ,ARTST_ACTVTY_NATN_CD AS artstActvtyNatnCd ,ARTST_ACTVTY_CITY_CD AS artstActvtyCityCd ,ARTST_SELF_INTRO AS artstSelfIntro ,ARTST_PROFILE_IMG_URL AS artstProfileImgUrl ,ARTST_HMPG_URL AS artstHmpgUrl ,ARTST_PROMTN_VIDEO_URL AS artstPromtnVideoUrl ,ARTST_FACEBOOK_URL AS artstFacebookUrl ,ARTST_INSTAGRAM_URL AS artstInstagramUrl ,REG_MBR_SQ AS regMbrSq ,REG_DT AS regDt ,UPDT_MBR_SQ AS updtMbrSq ,UPDT_DT AS updtDt ,USE_YN AS useYn ,DEL_YN AS delYn ,(SELECT MBR_NM FROM TBL_DA_MBR_M WHERE MBR_SQ = TBL.MBR_SQ) AS mbrNm FROM TBL_DA_ARTST_M TBL WHERE 1=1
AND MBR_SQ = #{mbrSq}
AND ARTST_SQ = #{artstSq}

mapper.update:
UPDATE TBL_DA_ARTST_M
ARTST_ACTVTY_NM = #{artstActvtyNm}, ARTST_ENGLS_NM = #{artstEnglsNm}, ARTST_ACTVTY_NATN_CD = #{artstActvtyNatnCd}, ARTST_ACTVTY_CITY_CD = #{artstActvtyCityCd}, ARTST_ACTVTY_PART_CD = #{artstActvtyPartCd}, ARTST_ACTVTY_CD = #{artstActvtyCd}, ARTST_SELF_INTRO = #{artstSelfIntro}, ARTST_PROFILE_IMG_URL= #{artstProfileImgUrl}, ARTST_HMPG_URL = #{artstHmpgUrl}, ARTST_PROMTN_VIDEO_URL= #{artstPromtnVideoUrl}, ARTST_FACEBOOK_URL = #{artstFacebookUrl}, ARTST_INSTAGRAM_URL = #{artstInstagramUrl}, UPDT_DT = NOW(),
WHERE MBR_SQ = #{mbrSq}
