mapper.select:
SELECT BNN_SQ ,BNN_DIV_CD ,BNN_TITLE ,BNN_MP_IMG_URL ,BNN_EP_IMG_URL ,BNN_MM_IMG_URL ,BNN_EM_IMG_URL ,BNN_M_LND_URL ,BNN_E_LND_URL ,DATE_FORMAT(BNN_START_DT, "%Y-%m-%d") as BNN_START_DT ,DATE_FORMAT(BNN_END_DT, "%Y-%m-%d") as BNN_END_DT ,DATE_FORMAT(REG_DT, "%Y-%m-%d") as REG_DT ,REG_MBR_SQ ,DATE_FORMAT(UPDT_DT, "%Y-%m-%d") as UPDT_DT ,UPDT_MBR_SQ ,USE_YN ,DEL_YN FROM TBL_DA_BNN_MGMT_M WHERE 1=1
AND BNN_DIV_CD = #{searchGubun}
AND USE_YN = 'Y' AND DEL_YN = 'N'

mapper.update:
/* 프로모션 영역 수정 */
	UPDATE
			TBL_DA_PROMO_MGMT_S
	SET
			BNN_DIV_CD = #{bnnDivCd}
			,PROMO_NM = #{promoNm}
			,PROMO_OD_DIV_CD = #{promoOdDivCd}
			,PROMO_OD_TYP_CD = #{promoOdTypCd}
	WHERE
			PROMO_SQ = #{promoSq}
