mapper.select:
SELECT AUTH_SQ AS authSq ,MBR_SQ AS mbrSq ,MBR_ID AS mbrId ,MBR_EMAIL AS mbrEmail ,MBR_PASSWRD AS mbrPasswrd ,MBR_NM AS mbrNm ,IFNULL(MBR_NCKNM,'-') AS mbrNcknm ,MBR_NATN_CD AS mbrNatnCd ,MBR_BIRTH AS mbrBirth ,( CASE WHEN MBR_SEX_CD = 'MALE' THEN '남' WHEN MBR_SEX_CD = 'FEMALE' THEN '여' WHEN MBR_SEX_CD IS NULL THEN '-' END ) AS mbrSexCd ,MBR_HOME_ADDR AS mbrHomeAddr ,MBR_DELIVRY_ADDR AS mbrDelivryAddr ,MBR_CP_NUM AS mbrCpNum ,MBR_CP_CERT_YN AS mbrCpCertYn ,MBR_CP_CERT_DATE AS mbrCpCertDate ,REG_MBR_SQ AS regMbrSq ,DATE_FORMAT(REG_DT,'%Y-%m-%d') AS regDt ,UPDT_MBR_SQ AS updtMbrSq ,DATE_FORMAT(UPDT_DT,'%Y-%m-%d') AS updtDt ,USE_YN AS useYn ,DEL_YN AS delYn ,( CASE WHEN MBR_SOCIAL_SORT = 'GEN' THEN '일반' WHEN MBR_SOCIAL_SORT = 'KAO' THEN '카카오' WHEN MBR_SOCIAL_SORT = 'GOG' THEN '구글' WHEN MBR_SOCIAL_SORT = 'NAV' THEN '네이버' END ) AS mbrSocialSort
FROM TBL_DA_MBR_M A WHERE 1=1
AND A.AUTH_SQ = #{memberGubun}
AND A.MBR_NM LIKE CONCAT('%',#{searchWord},'%')
AND A.MBR_ID LIKE CONCAT('%',#{searchWord},'%')
AND A.MBR_NCKNM LIKE CONCAT('%',#{searchWord},'%')
AND A.MBR_SQ = #{mbrSq}
AND A.USE_YN = #{useYn}
ORDER BY MBR_SQ DESC

SELECT count(*) FROM TBL_DA_MBR_M A WHERE 1=1
AND MBR_ID = #{mbrId}
AND MBR_NCKNM = #{mbrNcknm}

mapper.update:
UPDATE TBL_DA_MBR_M
AUTH_SQ =#{authSq}, MBR_EMAIL =#{mbrEmail}, MBR_PASSWRD =#{mbrPasswrd}, MBR_NATN_CD = #{mbrNatnCd}, MBR_BIRTH = #{mbrBirth}, MBR_SEX_CD = #{mbrSexCd}, MBR_HOME_ADDR = #{mbrHomeAddr}, MBR_DELIVRY_ADDR =#{mbrDelivryAddr}, MBR_CP_NUM = #{mbrCpNum}, UPDT_DT = NOW(), USE_YN = #{useYn}, DEL_YN = #{delYn},
WHERE MBR_SQ = #{mbrSq}

UPDATE TBL_DA_MBR_M
MBR_PASSWRD =#{mbrPasswrd}, UPDT_DT = NOW(),
WHERE MBR_SQ = #{mbrSq}

mapper.select:
SELECT
A.AUTH_SQ AS authSq ,A.MBR_SQ AS mbrSq ,A.MBR_ID AS mbrId ,A.MBR_EMAIL AS mbrEmail ,A.MBR_PASSWRD AS mbrPasswrd ,MBR_NM AS mbrNm ,IFNULL(A.MBR_NCKNM,'-') AS mbrNcknm ,A.MBR_NATN_CD AS mbrNatnCd ,A.MBR_BIRTH AS mbrBirth ,( CASE WHEN A.MBR_SEX_CD = 'MALE' THEN '남' WHEN A.MBR_SEX_CD = 'FEMALE' THEN '여' WHEN A.MBR_SEX_CD IS NULL THEN '-' END ) AS mbrSexCd ,A.MBR_HOME_ADDR AS mbrHomeAddr ,A.MBR_DELIVRY_ADDR AS mbrDelivryAddr ,A.MBR_CP_NUM AS mbrCpNum ,A.MBR_CP_CERT_YN AS mbrCpCertYn ,A.MBR_CP_CERT_DATE AS mbrCpCertDate ,A.REG_MBR_SQ AS regMbrSq ,DATE_FORMAT(A.REG_DT,'%Y-%m-%d') AS regDt ,A.UPDT_MBR_SQ AS updtMbrSq ,DATE_FORMAT(A.UPDT_DT,'%Y-%m-%d') AS updtDt ,A.USE_YN AS useYn ,A.DEL_YN AS delYn ,( CASE WHEN A.MBR_SOCIAL_SORT = 'GEN' THEN '일반' WHEN A.MBR_SOCIAL_SORT = 'KAO' THEN '카카오' WHEN A.MBR_SOCIAL_SORT = 'GOG' THEN '구글' WHEN A.MBR_SOCIAL_SORT = 'NAV' THEN '네이버' END ) AS mbrSocialSort
FROM TBL_DA_MBR_M A INNER JOIN TBL_DA_ARTST_M B ON A.MBR_SQ = B.MBR_SQ WHERE A.AUTH_SQ = '1'
AND A.MBR_ID = #{mbrId}
AND A.MBR_NM = #{mbrNm}
